def getParams(str: String): Map[String, String] = {
    val params: Array[(String, String)] = str.replace("\n", "").replace("\t", " ").trim.split(";").map { s =>
      def split(str: String, sep: String, other: List[String]): (String, String) = str.split(sep).map(s => s.trim) match {
        case vals if vals.length == 2 => vals(0) -> vals(1)
        case vals if other.isEmpty => "other_properties" -> str.replace("\t", "")
        case vals => split(str, other.head, other.tail)
      }

      split(s.trim, ":", List("=", "-", " "))
    }
    params.foldLeft(Map.empty[String, String]){ case (acc, (key, value)) => if(acc.contains(key)) acc.updated(key, acc(key) + ", " + value) else acc.updated(key, value)}
  }

def extractParams = udf[Map[String, String], String]{ s=> getParams(s)}
def p = udf[String, String]{ s => if(s==null) "" else s.replace("\n", "").replace("\t", " ") }
def aboutAging(str: String) = str.startsWith("age") || str.contains("age") || str.contains("long-lived") || str.contains("lifespan")
def extractAge = udf[Map[String, String], Map[String, String]]{ mp => mp.filter{ case (key ,value) => aboutAging(key.toLowerCase) || aboutAging(value.toLowerCase) }}

