def quantify(found: DataFrame, genotype: String = "parameters.strain") = { 
    import org.apache.spark.sql.functions._
    import org.apache.spark.sql.types._
    import org.apache.spark.sql._
    import spark.implicits._
    val schema = StructType(Seq(  StructField("strain", StringType, true), StructField("tissue", StringType, true)))
    //GSM	GSE	Species	Sequencer	Type	Sex	Age	Tissue	Extracted molecule	Strain	Comments
    val strain = new ColumnName(genotype).alias("Strain")
    found.withColumn("age", lit("")).withColumn("sex", lit("")).withColumn("parameters", from_json($"parameters", schema)).select($"GSM", $"GSE", $"model".alias("Sequencer"), $"organism".alias("Species"), $"parameters.tissue".alias("Type"), $"Sex", $"Age", $"parameters.tissue".alias("Tissue"), $"molecule".alias("Extracted molecule"),  strain, $"title".alias("Comments"))

}

